mapper.select:
select 

from orderitem
where order_id = #{orderId,jdbcType=VARCHAR}
  and product_id = #{productId,jdbcType=VARCHAR}

mapper.delete:
delete from orderitem
where order_id = #{orderId,jdbcType=VARCHAR}
  and product_id = #{productId,jdbcType=VARCHAR}

mapper.insert:
insert into orderitem (order_id, product_id, buynum
  )
values (#{orderId,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}, #{buynum,jdbcType=INTEGER}
  )

mapper.update:
update orderitem


    buynum = #{buynum,jdbcType=INTEGER},


where order_id = #{orderId,jdbcType=VARCHAR}
  and product_id = #{productId,jdbcType=VARCHAR}

update orderitem
set buynum = #{buynum,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=VARCHAR}
  and product_id = #{productId,jdbcType=VARCHAR}
